import "./ExpenseFilter.css";
import { useState } from "react";

const ExpenseFilter = (props) => {
  const [yearSelected, setYearSelected] = useState("d");

  function clickHandler(event) {
    setYearSelected(event.target.value);
    console.log("ano clicado")
  }

  props.onYearSelected(yearSelected);

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select>
          <option value="2022" onChange={clickHandler}>
            2022
          </option>
          <option value="2021" onChange={clickHandler}>
            2021
          </option>
          <option value="2020" onChange={clickHandler}>
            2020
          </option>
          <option value="2019" onChange={clickHandler}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpenseFilter;
